Scripts.OnLoad:
			        --SP and Crit % Monitor Size
                    getglobal(self:GetName().."High"):SetText("6");
    	    	    getglobal(self:GetName().."Low"):SetText("0");
	    	    	self:SetMinMaxValues(0,6);
		    	    self:SetValueStep(1);

Scripts.OnEnter:
                self:SetAttribute("type1", "item")
				self:SetAttribute("item", GetItemInfo(6948))
                homeLocation = GetBindLocation();
                GameTooltip:SetOwner(self, ANCHOR_TOPLEFT);
				GameTooltip:AddLine("Hearthstone: "..homeLocation)
				GameTooltip:Show()

